import { useEffect, useState } from "react";
import supabase from "./supabase";
import "./style.css";



const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

function Counter() {
  const [Counter, setCount]= useState(0)
  return (
  <div>
    <span style={{ fontSize:"40px" }}>8</span>
    <button className="btn btn-large" onClick={() => setCount((c)=> c+1)}>+1</button>
  </div>);
}

function App() {
  // defining state variable
  const [showForm, setShowForm] = useState(false);
  const [facts, setFacts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentCategory, setCurrentCategory] = useState("all");

  useEffect(
  function(){
    async function getFacts() {
    setIsLoading(true);
    let query = supabase.from("facts").select("*");

    if (currentCategory !== "all") {
      query = query.eq("category", currentCategory);
    }

    const { data: facts, error } = await query
      .order("votesInteresting", { ascending:false })
      .limit(600);
      if (!error) {
        setFacts(facts);
      } 
      else alert("La base de donnÃ©e est n'est pas accessible.");
      setIsLoading(false);
  }
  getFacts();
}, [currentCategory]);

  return (
    <>
    <Header show={showForm} setShowForm={setShowForm}/>
      {showForm ? <NewFactForm setFacts={setFacts} />: null}
      <main className="main">
      <CategoryFilter setCurrentCategory = {setCurrentCategory} />
      {isLoading ?( <Loader /> ) : (<FactList facts={facts} setFacts={setFacts}/> )}
      </main>
      <Footer/>
    </>
  );
}

function Loader(){
  return <p className="loading-message">loading...</p>
}


function Header({showForm, setShowForm}) {
  const appTitle = "Today I learned";
  return (
    <header className="header">
        <div clasName="logo">
          <img
            src=" logo.png"
            height="68"
            width="68"
            alt="Today I Learned Logo"
          />
          <h1>{appTitle}</h1>
        </div>

        <button className="btn btn-large btn-open" 
        onClick={()=> setShowForm((show)=> !show)}
        >
          {showForm ? "Close":"Share a fact"}          
          </button>
      </header>
  );
}

function Footer() {
  return (
    <footer className="footer">
      <hr className="horizontal-bar"/>
      <div className="footer-content">
        <p className="built-by">Built by Mamour DIENG Â©</p>
        <div className="socials">
          <a href="https://twitter.com/your-twitter-handle" target="_blank" rel="noopener noreferrer">
            <img src="/socials/twitter.svg" alt="Twitter" className="social-icon" />
          </a>
          <a href="https://linkedin.com/in/your-linkedin-profile" target="_blank" rel="noopener noreferrer">
            <img src="/socials/linkedin.svg" alt="LinkedIn" className="social-icon" />
          </a>
          <a href="https://github.com/your-github-profile" target="_blank" rel="noopener noreferrer">
