import { useEffect, useState } from "react";
import supabase from "./supabase";
import "./style.css";

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

function Counter() {
  const [Counter, setCount]= useState(0)
  return (
  <div>
    <span style={{ fontSize:"40px" }}>8</span>
    <button className="btn btn-large" onClick={() => setCount((c)=> c+1)}>+1</button>
  </div>);
}

function App() {
  // defining state variable
  const [showForm, setShowForm] = useState(false);
  const [facts, setFacts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentCategory, setCurrentCategory] = useState("all");

  useEffect(
  function(){
    async function getFacts() {
    setIsLoading(true);

    if (currentCategory !== "all") { const query = query.eq("category", currentCategory);

    const { data: facts, error } = await query
      .order("votesInteresting", { ascending:false })
      .limit(600);
      setFacts(facts);
    }

    setIsLoading(false);
  }
  getFacts();
}, [currentCategory]);

  return (
    <>
    <Header show={showForm} setShowForm={setShowForm}/>
      {showForm ? <NewFactForm setFacts={setFacts} />: null}

      <main className="main">
      <CategoryFilter setCurrentCategory = {setCurrentCategory} />
      {isLoading ? <Loader />:<FactList facts={facts}/> }
      </main>
    </>
  );
}

function Loader(){
  return <p className="loading-message">Loading...</p>


}


function Header({showForm, setShowForm}) {
  const appTitle = "Today I learned";
  return (
    <header className="header">
        <div clasName="logo">
          <img
            src=" logo.png"
            height="68"
            width="68"
            alt="Today I Learned Logo"
          />
          <h1>{appTitle}</h1>
        </div>

        <button className="btn btn-large btn-open" 
        onClick={()=> setShowForm((show)=> !show)}
        >
          {showForm ? "Close":"Share a fact"}          
          </button>
      </header>
  );
}
function isValidHttpUrl(string) {
  let url;

  try {
    url = new URL(string);
  } catch (_) {
    return false;  
  }

  return url.protocol === "http:" || url.protocol === "https:";
}

function NewFactForm({setFacts, setShowForm}) {
const [text, setText] = useState("");
const [source, setSource] = useState("http://example.com");
const [category, setCategory] = useState("");

function handleSubmit(e) {
  // prevent browser from reloading
e.preventDefault();
// check if data is valid
if (text && isValidHttpUrl(source) && category && text.length <= 200) {
// create a new fact object
const newFact = {
  id: Math.round(Math.random() * 1000000000),
  text: text,
  source,
  category: category,
  votesInteresting: 0,
  votesMindblowing: 0,
  votesFalse: 0,
  createdIn: new Date().getFullYear(),
};
// add the new fact to the UI: add the fact to the state
setFacts((facts)=> [newFact, ...facts]);
// reset input fields
setText("");
setSource("")
setCategory("");
}
}
return (
  <form className="fact-form" onSubmit={handleSubmit}>
  <input 
    type="text" 
    placeholder="Share a fact with the world..."
    value={text}
    onChange = {(e)=>setText(e.target.value)}/>

  <span>{200 - text.length}</span> {/* fix this!! */}

  <input 
    type="text" 
    placeholder="Trustworthy source..." 
    value = {source}
    onChange={(e)=> setSource(e.target.value)}/>

  <select value={category}
      onChange={(el)=> setCategory(el.target.value)}>
        <option value="">Choose category:</option>
        {CATEGORIES.map((cat)=> (<option 
        key = {cat.name}
        value={cat.name}>
          {cat.name.toUpperCase()}
        </option>))}
  </select>
  <button className="btn btn-large">Post</button>
</form>);
}

function CategoryFilter({ setCurrentCategory }) {
  return ( 
  <aside>
    <ul>
      <li className="category">
        <button className="btn btn-all-categories"
                onClick={() => setCurrentCategory("all")}>
          All</button>
      </li>
      {CATEGORIES.map((cat)=> (
        <li key={cat.name} className="category">
          <button 
          className="btn btn-category" 
          style= {{ backgroundColor: cat.color }}
          onClick={() => setCurrentCategory(cat.name)}
          >
            {cat.name}
          </button>
          </li>
      ))}
    </ul>
  </aside>
  );
}

function FactList({facts}) {
  return (
  <section><ul className="facts-list">{
    facts.map((fact)=>
    (<Fact key={fact.id} fact={fact} />
  ))}
    </ul>
    <p> Il y'a actuellement {facts.length} faits dans la base de données. Ajoutes le tien! </p>
    </section>
    );
}

function Fact({ fact }) {

  return <li className="fact">
      <p>
        {fact.text}
        <a
          className="source"
          href={fact.source}
          target="_blank"
          >(Source)</a
        >
      </p>
      <span className="tag" style={{
        backgroundColor: CATEGORIES.find((cat)=> cat.name === fact.category).color,}}
        >{fact.category}</span
      >
      <div className="vote-buttons">
        <button>👍 {fact.votesInteresting}</button>
        <button>🤯 {fact.votesMindblowing}</button>
        <button>⛔️ {fact.votesFalse}</button>
      </div>
    </li>

}
export default App;
